INSERT INTO exercise_descriptor_i18n (fk_exercise_descriptor_id, locale, name) VALUES 
(1,"en_US","Can read a very short, rehearsed statement – e.g. to introduce a speaker, propose a toast"),
(1,"de_DE","Kann ein kurzes, eingeübtes Statement verlesen, um z. B. einen Redner vorzustellen oder einen Toast auszubringen"),
(1,"es_ES","Es capaz de leer un comunicado breve y previamente ensayado; por ejemplo, presentar a un hablante o proponer un brindis"),
(1,"fr_FR","Peut lire un texte très bref et répété, par exemple pour présenter un conférencier, proposer un toast"),
(2,"en_US","Can make an introduction and use basic greeting and leave-taking expressions"),
(2,"de_DE","Kann jemanden vorstellen und einfache Gruß- und Abschiedsformeln gebrauchen"),
(2,"es_ES","Se presenta y utiliza saludos y expresiones de despedida básicos"),
(2,"fr_FR","Peut présenter quelqu'un et utiliser des expressions élémentaires de salutation et de congé"),
(3,"en_US","Can ask how people are and react to news"),
(3,"de_DE","Kann jemanden nach dem Befinden fragen und auf Neuigkeiten reagieren"),
(3,"es_ES","Pregunta cómo están las personas y expresa sus reacciones ante las noticias"),
(3,"fr_FR","Peut demander à quelqu'un de ses nouvelles et y réagir"),
(4,"en_US","Can describe him/herself, what he/she does and where he/she lives"),
(4,"de_DE","Kann sich selbst beschreiben und sagen, was er/sie beruflich tut und wo er/sie wohnt"),
