         ('156', 'eu_ES', 'Arazo bat era argian azaltzeko gai da, jatorri eta ondorioei buruzko espekulazioa egin, eta ikuspuntu desberdinen abantailak eta desabantailak ponderatuz'),
         ('157', 'eu_ES', 'Elkarrizketa etorri eta efektibo bat gauzatzeko gai da, prestatutako galderetatik espontaneoki atera eta ildora lortutako emaitzen arabera itzuliz'),
         ('158', 'eu_ES', 'Bere ideiak eta iritziak zehaztasunez adierazi ditzake eta argudio lerroak aurkeztu eta hauei era konbentzigarrian erantzuna emateko gai da'),
         ('159', 'eu_ES', 'Bere iritziarekin parte hartu, bere iritzia mantendun, aukera edo alternatibak ebaluatu eta hipotesiak egin eta hauei erantzuna emateko gai da'),
         ('160', 'eu_ES', 'Mezu arruntak bidali edo jasotzeko GAI DA, hauek garbitasun nahikoarekin adierazten ez direnean azalpen gehiago eskatuz, oso noizbehinkako gaizkiulertuak jazoz'),
         ('161', 'eu_ES', 'Bere lan esparruko informazio zehatza eman eta eskakizun zehatzak egiteko GAI DA'),
         ('162', 'eu_ES', 'Bere ikuspuntua efektiboki adierazteko GAI DA, beharrezkoa bada zerbitzu baten beharra justifikatuz eta zerbitzuren xehetasunak emanez'),
         ('163', 'eu_ES', 'Bere lan esparrutik kanpo dauden galderei erantzuna emateko GAI DA'),
         ('164', 'eu_ES', 'Bisitari batekin elkarrizketa bat mantentzeko GAI DA bere konpetentzia edo jakintza esparruan baldin bada'),
         ('165', 'eu_ES', 'Informazio zehatza eman eta gerta daitezkeen arazoen gehiengoari aurre egiteko GAI DA'),
         ('166', 'eu_ES', 'Bere lan esparrutik kanpo dauden gauzei buruzko galderak egiteko GAI DA (adib. kanpoko aholkularitza legala)'),
         ('167', 'eu_ES', 'Arazo konplexu edo delikatuei buruzko aholkua eman edo hauei aurre egiteko GAI DA (Adib. lege edo finantza egoerak)'),
         ('168', 'eu_ES', 'Elkarrizketa batean guztiz parte hartzeko gai da, elkarrizketatu edo elkarrizketatzaile gisa, lantzen ari den puntua laguntzarik gabe eta etorriarekin landuz edo zabalduz, eta mozketak ondo maneiatuz'),
         ('169', 'eu_ES', 'Aurreikusi ezin diren galderei aurre egiteko GAI DA'),
         ('170', 'eu_ES', 'Bere lan esparruko mintegi edo bileretan era efektiboan parte hartzeko GAI DA'),
         ('171', 'eu_ES', 'Ikuspuntu baten alde edo kontra era efektiboan hitz egiteko GAI DA, eta hiztegi nahikoa du bere lanari buruzko gauzei buruz hitz egin edo eztabaidatzeko'),
         ('172', 'eu_ES', 'Telefonoa konfidantzarekin erabiltzeko GAI DA, sarea txarra edo hizlariaren doinua ez-estandarra baldin bada ere');";

    $DB->_insert($query);
    
    echo "Manually rebuild your searchindex file (Search.php, function reCreateIndex()) to search Basque descriptors.\n";	
}

